import { IRoute } from './route';
import type { UploadOptions } from 'graphql-upload';
import { GraphQLSchema } from 'graphql';
export interface IGraphqlRouter {
    type: string;
    graphiql: boolean;
    pretty: boolean;
    path: string;
    schema: string;
    graphqlUpload?: UploadOptions;
    getRoutes(): IRoute[];
    getResolver(): object;
    rootResolveFn?(root: any, args: any, context: any, info: any): void;
    customFormatErrorFn?(error: any): object;
    customDirectives?: {
        name: string;
        directive: (schema: GraphQLSchema, directiveName: string) => GraphQLSchema;
    }[];
}
